import React, { useState, useEffect } from 'react'; 
import {
    Box,
    Card,
    CardContent,
    Typography,
    Button,
    Table,
    TableHead,
    TableRow,
    TableCell,
    TableBody,
    TableContainer,
    Paper,
    Alert,
    CircularProgress,
    Chip,
    IconButton,
    MenuItem,
    Select,
    FormControl,
    InputLabel
} from '@mui/material';
import { UploadFile, Delete } from '@mui/icons-material';

// Tipos
interface Document {
    id: number;
    name: string;
    type: string;
    accessLevel: 'LIVRE' | 'RESTRITO' | 'CONFIDENCIAL';
    uploadedAt: string;
}

type UserAccessLevel = 'BASICO' | 'INTERMEDIARIO' | 'TOTAL';

const DocumentsManagement: React.FC = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [documents, setDocuments] = useState<Document[]>([]);
    const [userAccessLevel, setUserAccessLevel] = useState<UserAccessLevel>('BASICO');

    // Estado para upload
    const [newDocName, setNewDocName] = useState('');
    const [newDocType, setNewDocType] = useState('PDF');
    const [newDocAccessLevel, setNewDocAccessLevel] = useState<'LIVRE' | 'RESTRITO' | 'CONFIDENCIAL'>('LIVRE');

    // Simulação de dados carregados da API
    useEffect(() => {
        setLoading(true);
        const fetchedDocuments: Document[] = [
            { id: 1, name: 'Documento Público', type: 'PDF', accessLevel: 'LIVRE', uploadedAt: new Date().toLocaleDateString('pt-BR') },
            { id: 2, name: 'Documento Restrito', type: 'DOCX', accessLevel: 'RESTRITO', uploadedAt: new Date().toLocaleDateString('pt-BR') },
            { id: 3, name: 'Documento Confidencial', type: 'XLSX', accessLevel: 'CONFIDENCIAL', uploadedAt: new Date().toLocaleDateString('pt-BR') },
        ];
        setDocuments(fetchedDocuments);
        setLoading(false);

        // Defina aqui o nível de acesso do usuário logado
        setUserAccessLevel('INTERMEDIARIO');
    }, []);

    // Filtrar documentos pelo nível de acesso
    const filteredDocuments = documents.filter(doc => {
        if (userAccessLevel === 'BASICO') return doc.accessLevel === 'LIVRE';
        if (userAccessLevel === 'INTERMEDIARIO') return doc.accessLevel === 'LIVRE' || doc.accessLevel === 'RESTRITO';
        if (userAccessLevel === 'TOTAL') return true;
        return false;
    });

    // Filtrar opções de acesso para upload
    const allowedUploadLevels = () => {
        if (userAccessLevel === 'BASICO') return ['LIVRE'];
        if (userAccessLevel === 'INTERMEDIARIO') return ['LIVRE', 'RESTRITO'];
        if (userAccessLevel === 'TOTAL') return ['LIVRE', 'RESTRITO', 'CONFIDENCIAL'];
        return ['LIVRE'];
    };

    const handleUpload = () => {
        if (!newDocName) {
            setError('Informe o nome do documento.');
            return;
        }
        const newDocument: Document = {
            id: documents.length + 1,
            name: newDocName,
            type: newDocType,
            accessLevel: newDocAccessLevel,
            uploadedAt: new Date().toLocaleDateString('pt-BR')
        };
        setDocuments(prev => [...prev, newDocument]);
        setNewDocName('');
        setNewDocType('PDF');
        setNewDocAccessLevel('LIVRE');
        setError(null);
    };

    const handleDelete = (docId: number) => {
        setDocuments(prev => prev.filter(doc => doc.id !== docId));
    };

    return (
        <Card>
            <CardContent>
                <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                    <Typography variant="h5">Gerenciamento de Documentos</Typography>
                </Box>

                {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

                {/* Formulário de upload */}
                <Box display="flex" gap={2} mb={3}>
                    <input
                        type="text"
                        placeholder="Nome do Documento"
                        value={newDocName}
                        onChange={(e) => setNewDocName(e.target.value)}
                        style={{ padding: '6px', flex: 2 }}
                    />
                    <input
                        type="text"
                        placeholder="Tipo (PDF, DOCX, XLSX)"
                        value={newDocType}
                        onChange={(e) => setNewDocType(e.target.value)}
                        style={{ padding: '6px', flex: 1 }}
                    />
                    <FormControl sx={{ minWidth: 150 }}>
                        <InputLabel>Nível de Acesso</InputLabel>
                        <Select
                            value={newDocAccessLevel}
                            label="Nível de Acesso"
                            onChange={(e) => setNewDocAccessLevel(e.target.value as any)}
                        >
                            {allowedUploadLevels().map(level => (
                                <MenuItem key={level} value={level}>{level}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <Button variant="contained" startIcon={<UploadFile />} onClick={handleUpload}>
                        Enviar
                    </Button>
                </Box>

                {loading ? (
                    <Box display="flex" justifyContent="center" alignItems="center" minHeight="150px">
                        <CircularProgress />
                    </Box>
                ) : (
                    <TableContainer component={Paper}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Nome</TableCell>
                                    <TableCell>Tipo</TableCell>
                                    <TableCell>Nível de Acesso</TableCell>
                                    <TableCell>Data Upload</TableCell>
                                    <TableCell align="center">Ações</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {filteredDocuments.map(doc => (
                                    <TableRow key={doc.id}>
                                        <TableCell>{doc.name}</TableCell>
                                        <TableCell>{doc.type}</TableCell>
                                        <TableCell>
                                            <Chip label={doc.accessLevel} color="primary" size="small"/>
                                        </TableCell>
                                        <TableCell>{doc.uploadedAt}</TableCell>
                                        <TableCell align="center">
                                            <IconButton color="error" onClick={() => handleDelete(doc.id)}>
                                                <Delete />
                                            </IconButton>
                                        </TableCell>
                                    </TableRow>
                                ))}
                                {filteredDocuments.length === 0 && (
                                    <TableRow>
                                        <TableCell colSpan={5} align="center">
                                            Nenhum documento disponível para seu nível de acesso.
                                        </TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}
            </CardContent>
        </Card>
    );
};

export default DocumentsManagement;
