import React, { useState, useEffect, useCallback } from "react";
import {
  Box,
  Typography,
  Button,
  CircularProgress,
  Alert,
  Chip,
  List,
  ListItem,
  ListItemText,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from "@mui/material";
import { Add, Download } from "@mui/icons-material";
import { apiService, Document, DocumentLevel } from "../services/api";

interface Props {
  userEmail: string;
}

const DocumentsManagement: React.FC<Props> = ({ userEmail }) => {
  const [documents, setDocuments] = useState<Document[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [uploadDialogOpen, setUploadDialogOpen] = useState(false);
  const [newDoc, setNewDoc] = useState({
    title: "",
    description: "",
    documentLevel: DocumentLevel.LIVRE,
  });
  const [file, setFile] = useState<File | null>(null);
  const [submitting, setSubmitting] = useState(false);

  const fetchDocuments = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiService.getDocuments(userEmail);
      setDocuments(response.documents);
    } catch (err: any) {
      setError(err.message || "Erro ao obter documentos");
    } finally {
      setLoading(false);
    }
  }, [userEmail]);

  useEffect(() => {
    fetchDocuments();
  }, [fetchDocuments]);

  const handleDownload = async (doc: Document) => {
    try {
      const blob = await apiService.downloadDocument(doc.id, userEmail);
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", doc.filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err: any) {
      alert(err.message || "Erro ao baixar documento");
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selected = e.target.files?.[0] || null;
    if (selected) setFile(selected);
  };

  const handleUpload = async () => {
    if (!file || !newDoc.title) {
      setError("Preencha título e selecione um arquivo");
      return;
    }
    setSubmitting(true);
    setError(null);
    try {
      await apiService.uploadDocuments(
        newDoc.title,
        newDoc.description,
        newDoc.documentLevel,
        userEmail,
        file
      );
      setUploadDialogOpen(false);
      setFile(null);
      setNewDoc({ title: "", description: "", documentLevel: DocumentLevel.LIVRE });
      fetchDocuments();
    } catch (err: any) {
      setError(err.response?.data?.detail || err.message || "Erro ao enviar documento");
    } finally {
      setSubmitting(false);
    }
  };

  const getLevelChip = (level: DocumentLevel) => {
    const mapping = {
      LIVRE: { label: "Livre", color: "success" as const },
      RESTRITO: { label: "Restrito", color: "warning" as const },
      CONFIDENCIAL: { label: "Confidencial", color: "error" as const },
    };
    return <Chip label={mapping[level].label} color={mapping[level].color} size="small" />;
  };

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <Typography variant="h5">Documentos ({documents.length})</Typography>
        <Button variant="contained" startIcon={<Add />} onClick={() => setUploadDialogOpen(true)}>
          Novo Documento
        </Button>
      </Box>

      {loading && <CircularProgress />}
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      <List>
        {documents.length === 0 && !loading ? (
          <Typography>Nenhum documento disponível.</Typography>
        ) : (
          documents.map((doc) => (
            <ListItem
              key={doc.id}
              secondaryAction={
                <Button
                  variant="contained"
                  startIcon={<Download />}
                  onClick={() => handleDownload(doc)}
                >
                  Baixar
                </Button>
              }
            >
              <ListItemText
                primary={
                  <Box display="flex" alignItems="center" gap={1}>
                    <span>{doc.title}</span>
                    {getLevelChip(doc.document_level)}
                  </Box>
                }
                secondary={doc.description}
              />
            </ListItem>
          ))
        )}
      </List>

      {/* Dialog de upload */}
      <Dialog open={uploadDialogOpen} onClose={() => setUploadDialogOpen(false)} fullWidth maxWidth="sm">
        <DialogTitle>Novo Documento</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Título"
            value={newDoc.title}
            onChange={(e) => setNewDoc({ ...newDoc, title: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Descrição"
            value={newDoc.description}
            onChange={(e) => setNewDoc({ ...newDoc, description: e.target.value })}
            margin="normal"
          />
          <FormControl fullWidth margin="normal">
            <InputLabel>Nível do Documento</InputLabel>
            <Select
              value={newDoc.documentLevel}
              onChange={(e) => setNewDoc({ ...newDoc, documentLevel: e.target.value as DocumentLevel })}
            >
              <MenuItem value={DocumentLevel.LIVRE}>Livre</MenuItem>
              <MenuItem value={DocumentLevel.RESTRITO}>Restrito</MenuItem>
              <MenuItem value={DocumentLevel.CONFIDENCIAL}>Confidencial</MenuItem>
            </Select>
          </FormControl>
          <Box mt={2}>
            <input type="file" onChange={handleFileChange} />
            {file && <Typography>Selecionado: {file.name}</Typography>}
          </Box>
          {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setUploadDialogOpen(false)}>Cancelar</Button>
          <Button onClick={handleUpload} variant="contained" disabled={submitting}>
            {submitting ? <CircularProgress size={20} /> : "Enviar"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DocumentsManagement;
