import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import {
  Box,
  Button,
  CircularProgress,
  Typography,
  List,
  ListItem,
  ListItemText,
  Chip,
  Alert,
} from "@mui/material";
import { Download } from "@mui/icons-material";

interface Document {
  id: number;
  title: string;
  filename: string;
  description?: string;
  document_level: string;
  uploaded_at: string;
  uploaded_by: number;
}

interface DocumentAccessResponse {
  documents: Document[];
  user_acess_level: string;
  total_available: number;
}

interface Props {
  userEmail: string;
}

const DocumentsManagement: React.FC<Props> = ({ userEmail }) => {
  const [documents, setDocuments] = useState<Document[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchDocuments = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get<DocumentAccessResponse>(
        `/api/documents?user_email=${encodeURIComponent(userEmail)}`
      );
      setDocuments(response.data.documents);
    } catch (err: any) {
      console.error(err);
      setError(
        err.response?.data?.detail || "Erro ao buscar documentos do servidor"
      );
    } finally {
      setLoading(false);
    }
  }, [userEmail]);

  const handleDownload = async (documentId: number, filename: string) => {
    try {
      const response = await axios.get(`/api/documents/${documentId}/download`, {
        params: { user_email: userEmail },
        responseType: "blob",
      });

      // Criar link para download
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err: any) {
      console.error(err);
      alert(err.response?.data?.detail || "Erro ao baixar documento");
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, [fetchDocuments]);

  if (loading) return <CircularProgress />;
  if (error) return <Alert severity="error">{error}</Alert>;

  return (
    <Box>
      <Typography variant="h5" mb={2}>
        Documentos acessíveis ({documents.length})
      </Typography>
      {documents.length === 0 && (
        <Typography>Nenhum documento disponível.</Typography>
      )}
      <List>
        {documents.map((doc) => (
          <ListItem
            key={doc.id}
            secondaryAction={
              <Button
                variant="contained"
                color="primary"
                startIcon={<Download />}
                onClick={() => handleDownload(doc.id, doc.filename)}
              >
                Baixar
              </Button>
            }
          >
            <ListItemText
              primary={
                <Box display="flex" alignItems="center" gap={1}>
                  <span>{doc.title}</span>
                  <Chip label={doc.document_level} size="small" />
                </Box>
              }
              secondary={doc.description}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default DocumentsManagement;
